import React, { useState, useEffect } from 'react';
import { AlertCircle, Mic, MicOff } from 'lucide-react';
import { toast } from 'react-toastify';
import { useSelector } from 'react-redux';
import { useLocation } from 'react-router-dom';
import { API_URL } from '../../utils/utils';

 const VoiceDetection = () => {
  
  const location = useLocation();
  const isHomePage = location.pathname === '/';

  const {currentUser} = useSelector((state)=>state.user);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [alert, setAlert] = useState('');
  const [safetyKeywords, setSafetyKeywords] = useState(['help', 'emergency', 'danger', 'stop', 'police', 'save', 'please']);

  useEffect(() => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech recognition not supported in this browser. Please use Chrome.');
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      console.log('Speech recognition started');
      setIsListening(true);
    };

    recognition.onend = () => {
      console.log('Speech recognition ended');
      setTranscript('')
      setIsListening(false);
    };

    recognition.onresult = async (event) => {
      const currentTranscript = Array.from(event.results)
        .map(result => result[0].transcript)
        .join(' ')
        .toLowerCase();

      setTranscript(currentTranscript);

      const detectedKeyword = safetyKeywords.find(keyword => currentTranscript.includes(keyword.toLowerCase()));

      if (detectedKeyword) {
        setAlert(`Keyword detected: ${detectedKeyword}`);
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
        
        setIsListening(false);
        recognition.stop();
        await sendAlert();
      }
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
    };

    if (isListening) {
      recognition.start();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening, safetyKeywords]);
  
  const sendAlert = async () => {
    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0,
        });
      });

      const coords = [position.coords.longitude, position.coords.latitude];
      console.log(coords);
      const res = await fetch(`${API_URL}api/feature/send-alert`,{
        credentials:'include',
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({lon:position.coords.longitude, lat:position.coords.latitude})
      }); 

      const data = await res.json();
      if(res.status===200){
        toast.success("SOS Alert sent to your emergency contacts");
      }
      else{
        toast.error(data.message);
      }
    } 
    catch (err) {
      toast.error(err.message);
      console.error('Error calling API:', err);
    }
  };

  return (
    currentUser && !currentUser?.isAdmin && !isHomePage &&
    
    <>
      <div
      onClick={() => setIsListening(!isListening)}
      className="fixed bottom-4 right-36 w-12 h-12 bg-red-500 rounded-full flex items-center justify-center shadow-lg cursor-pointer text-white hover:bg-red-600 transition-colors"
     >
      {isListening ? <MicOff /> : <Mic />}
    </div>
    {/* <div className="m-4 bg-white border border-gray-200 rounded-xl p-6 shadow-lg text-gray-900 max-w-2xl mx-auto transition-transform hover:scale-105">
        <h3 className="text-xl font-semibold text-gray-800 mb-4">Transcript</h3>
        <p className="leading-relaxed text-gray-700">{transcript}</p>
    </div> */}
      
    </>
  );
};
export default VoiceDetection;
